import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Myclass from './ApplyFormClass';
import { Snackbar, SnackbarContent, Button } from '@material-ui/core';

import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogTitle from "@material-ui/core/DialogTitle";

import CameraIcon from '@material-ui/icons/CameraAltRounded';
import noTrain from './noTrain.jpg';
import { fetchPostStudent, fetchPostClassMember } from '../../api';
import axios from 'axios';
import Airtable from 'airtable';

const base = new Airtable({ apiKey: 'keyA7EKdngjou4Dgy' }).base('appcXtOTPnE4QWIIt');
const tableClass = base('ClassDay');


function createData(id, class_name) {
    return { id, class_name };
}

const IP = "http://localhost:8080";

const styles = theme => ({
    root:{
        margin:'auto',
        width:'40%',
    },
    container: {
        color: 'white',
    },
    photo: {
        width: '200px',
        height: '200px',
        marginLeft: '38%',
        marginTop: theme.spacing.unit * 10,
    },
    input: {
        outline: 'none',
        opacity: 0,
        width:'47%',
    },
    train:{
        marginTop: theme.spacing.unit * 2,
    },
    button: {
        display: 'flex',
        border: '1px #FFBF5F solid',
        borderRadius: '30px',
        color: '#FFBF5F',
        margin: 'auto',
    },
    rightIcon: {
        marginLeft: theme.spacing.unit,
    },
    textFieldLeft: {
        marginLeft: '14%',
        marginTop: theme.spacing.unit * 2,
        color: 'white',
        width: '260px',
    },
    textFieldRight: {
        marginLeft: theme.spacing.unit * 6,
        marginTop: theme.spacing.unit * 2,
        color: 'white',
        width: '260px',
    },
    textFieldFull: {
        marginLeft: '14%',
        marginTop: theme.spacing.unit * 2,
        color: 'white',
        width: '570px',
    },
    form: {
        width: '800px',
        margin: 'auto',
    },
});

class OutlinedTextFields extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            class_id: null,
            student_name: '',
            student_id: '',
            student_grade: '',
            student_phone: '',
            student_birth: '1998-07-13',
            student_school: '',
            student_email: '',
            student_parent: '',
            student_parent_phone: '',
            student_address: '',
            error1: false,
            error2: false,
            errorMessage1: '',
            errorMessage2: '',
            imgUrl:noTrain,
            open: false,
            openSnack: false,
            classDaydata:[],
        };
    }

    //for compare classDay id with class_id
    componentDidMount() {
        //classDay table
        tableClass.select({
            view: "Grid view",
        }).eachPage((records, fetchNextPage) => {
            this.setState({ records });
            const class_id = this.state.records.map((record, index) => record.fields['class_id']);
            const record_id = this.state.records.map((record, index) => record.id);

            var temp = [];
            for (var index = 0; index < class_id.length; index++) {
                temp.push(createData(class_id[index], record_id[index]));
            }
            this.setState({ classDaydata: temp });
            fetchNextPage();
        }
        );
    }

    //https://medium.com/@ruthmpardee/passing-data-between-react-components-103ad82ebd17
    myCallback = (dataFromChild) => {
        //this.setState({ class_id: dataFromChild }); 
        for (var index = 0; index < this.state.classDaydata.length; index++) {
            if(dataFromChild == this.state.classDaydata[index].class_name){
                this.setState({ class_id: this.state.classDaydata[index].id }); 
            }
        }
    }


    handleChange = name => event => {
        this.setState({
            [name]: event.target.value
        });
        //console.log(this.state);
    };
    // _downloadTxtFile = () => {
    //     var element = document.createElement("a");
