import React from 'react';
import PropTypes from 'prop-types';
import {
    AppBar, Toolbar, Button, InputBase,
    FormControl, Select, MenuItem,
    Dialog, DialogActions, DialogContent, DialogTitle,
    Input, IconButton,
    Snackbar, SnackbarContent
} from '@material-ui/core';
import { fade } from '@material-ui/core/styles/colorManipulator';
import { withStyles } from '@material-ui/core/styles';
import { NavLink } from "react-router-dom";
import Airtable from 'airtable';
import classNames from 'classnames';

import pin from './pin.png';
import Drawer from '../Drawer/Drawer';

//icon
import SearchIcon from '@material-ui/icons/Search';
import Home from '@material-ui/icons/HomeRounded';
import LogoutIcon from '@material-ui/icons/DirectionsWalkOutlined';
import Face from '@material-ui/icons/FaceOutlined';
import InfoIcon from '@material-ui/icons/Info';
import CloseIcon from '@material-ui/icons/Close';
import CheckIcon from '@material-ui/icons/Check';
import MergeIcon from '@material-ui/icons/MergeTypeRounded'

import axios from 'axios';

const base = new Airtable({ apiKey: 'keyA7EKdngjou4Dgy' }).base('appcXtOTPnE4QWIIt');
const TABLE_NAME = 'Student';
const table = base(TABLE_NAME);
const IP = "http://localhost:8080";

// this.Axios = axios.create({
//         baseURL: IP,
//         headers:{'content-type':'application/json','Access-Control-Allow-Origin':'*'}
//       });


const variantIcon = {
    warning: InfoIcon,
};
//snackBar
const styles1 = theme => ({
    warning: {
        backgroundColor: '#FFBF5F',
    },
    check: {
        fontSize: 20,
        color: '#111B24',
    },
    close: {
        fontSize: 20,
        color: '#111B24',
    },
    iconVariant: {
        opacity: 0.9,
        marginRight: theme.spacing.unit,
    },
    message: {
        display: 'flex',
        alignItems: 'center',
        color: '#111B24',
    },
});

function MySnackbarContent(props) {
    const { classes, className, message, onClose, variant, ...other } = props;
    const Icon = variantIcon[variant];
    const {stu_id}=props;
    console.log(props);
    return (
        <SnackbarContent
            className={classNames(classes[variant], className)}
            message={
                <span className={classes.message}>
                    <Icon className={classNames(classes.icon, classes.iconVariant)} />
                    {message}
                </span>
            }
            action={[
                <IconButton
                    key="close"
                    aria-label="Close"
                    color="inherit"
                    onClick={onClose}
                >
                    <NavLink className={classes.check}style={{ textDecoration: 'none' }} 
                    activeClassName="active" to={{pathname:'/student', 
                    aboutProps:{name:stu_id}}}>
                        <CheckIcon />
                    </NavLink>
                </IconButton>,
                <IconButton
                    key="close"
                    aria-label="Close"
                    color="inherit"
                    onClick={onClose}
                >
                    <CloseIcon className={classes.close} />
                </IconButton>
            ]}
            {...other}
        />
    );
}

MySnackbarContent.propTypes = {
    classes: PropTypes.object.isRequired,
    className: PropTypes.string,
    message: PropTypes.node,
    onClose: PropTypes.func,
    variant: PropTypes.oneOf(['success', 'warning', 'error', 'info']).isRequired,
};

const MySnackbarContentWrapper = withStyles(styles1)(MySnackbarContent);


//Appbar
const styles = theme => ({
    root: {
        width: '100%',
    },
    grow: {
        flexGrow: 1,
    },
    menuButton: {
        marginLeft: -12,
